import React from 'react';
import { withRouter } from 'react-router-dom';

function Login() {
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');

    function handleEmailChange(e) {
        setEmail(e.target.value);
    }

    function handlePasswordChange(e) {
        setPassword(e.target.value);
    }

    function handleSubmit(e) {
        e.preventDefault();
        if (!email || !password) {
            return;
        }
        // onLogin(email, password);
        resetForm();
    }

    function resetForm() {
        setEmail('');
        setPassword('');
    }

    return (
        <div className="login">
            <form onSubmit={handleSubmit} className="login__form">
                <div className="login__cont">
                    <h3 className="login__subtitle">Логин</h3>
                    <input className="login__input" placeholder="Логин" onChange={handleEmailChange} required />
                </div>

                <h3 className="login__subtitle">Пароль</h3>
                <input className="login__input" placeholder="Пароль" onChange={handlePasswordChange} required />
                <button className="login__submit">Войти</button>
            </form>
        </div>
    );
}

export default withRouter(Login);
