import React from 'react';
import { withRouter } from 'react-router-dom';

function Login({ onLogin }) {
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [emailWrong, setEmailWrong] = React.useState(true);
    const [passwordWrong, setPasswordWrong] = React.useState(true);
    const [emailError, setEmailError] = React.useState('');
    const [passwordError, setPasswordError] = React.useState('');
    const [isActive, setIsActive] = React.useState(false);

    React.useEffect(() => {
        setIsActive(false);
        if (!emailWrong && !passwordWrong) {
            setIsActive(true);
        }
    }, [emailWrong, passwordWrong])

    function handleEmailChange(e) {
        setEmail(e.target.value);
        validateEmail(e.target.value);
    }

    function handlePasswordChange(e) {
        setPassword(e.target.value);
        validatePassword(e.target.value);
    }

    function handleSubmit(e) {
        e.preventDefault();
        if (!email || !password) {
            return;
        }
        onLogin(email, password);
        resetForm();
    }

    function resetForm() {
        setEmail('');
        setPassword('');
    }

    function validateEmail(email) {
        const re = /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
        if (!email) {
            setEmailWrong(true);
            setEmailError('Поле не может быть пустым');
        } else if (!re.test(String(email).toLowerCase())) {
            setEmailWrong(true);
            setEmailError('Некорректный email');
        } else {
            setEmailWrong(false);
            setEmailError('');
        }
    }

    function validatePassword(password) {
        console.log(password.length)
        if (!password) {
            setPasswordWrong(true);
            setPasswordError('Поле не может быть пустым');
        } else if (password.length < 8) {
            setEmailWrong(true);
            setEmailError('Пароль не может быть короче 8 символов');
        } else {
            setPasswordWrong(false);
            setPasswordError('');
        }
    }

    return (
        <div className="login">
            <form onSubmit={handleSubmit} className="login__form">
                <div className="login__cont">
                    <div className="login__div">
                        <h3 className="login__subtitle">Логин:</h3>
                        <input className="login__input" onChange={handleEmailChange} required />
                        {(emailWrong && emailError) && <div className="login__error">{emailError}</div>}
                    </div>

                </div>
                <div className="login__cont">
                    <div className="login__div">
                        <h3 className="login__subtitle">Пароль:</h3>
                        <input type="password" className="login__input" onChange={handlePasswordChange} required />
                        {(passwordWrong && passwordError) && <div className="login__error">{passwordError}</div>}
                    </div>

                </div>
                <button className="" className={isActive ? "login__submit login__active" : "login__submit"} disabled={isActive ? false : true}>Войти</button>
            </form>
        </div>
    );
}

export default withRouter(Login);
