import React from 'react';
import PopupWithForm from '../PopupWithForm/PopupWithForm';
import * as api from '../../utils/api.js';

function AddPopup({ isOpen, onClose, onAddPlace }) {
    const [title, setTitle] = React.useState('');
    const [link, setLink] = React.useState('');

    function handleTitleChange(e) {
        setTitle(e.target.value);
    }

    function handleLinkChange(e) {
        setLink(e.target.value);
    }

    function handleSubmit(e) {
        e.preventDefault();
        console.log(title, link)
        api.addClass(title, link)
            .then((data) => {
                console.log(data)
            })
            .catch(err => console.log(err));
        setTitle('');
        setLink('');
    }

    return (
        <PopupWithForm name='add-card' title='💖 Добавить аудиторию 💖' isOpen={isOpen} onClose={onClose} buttonText="Сохранить" onSubmit={handleSubmit}>
            <input
                value={title || ''}
                onChange={handleTitleChange}
                id="title-input"
                type="text"
                placeholder="Номер комнаты"
                name="popup__title"
                className="popup__text popup__text_type_title"
                required
                minLength="2"
                maxLength="30" />
            <span className="title-input-error popup__error"></span>
            <input
                value={link || ''}
                onChange={handleLinkChange}
                id="url-input"
                placeholder="Вместимость"
                name="popup__link"
                className="popup__text popup__text_type_link"
                required />
            <span className="url-input-error popup__error"></span>
            <label htmlFor="checkbox" className="input__subtitle">Инструменты</label>
            <input
                type="checkbox"
                id="checkbox"
                // name="subscribe"
                // value="newsletter"
                required />
            <span className="url-input-error popup__error"></span>
            {/* <input
                value={title || ''}
                onChange={handleTitleChange}
                // id="title-input"
                type="number"
                placeholder="Day"
                name="popup__title"
                className="popup__text popup__text_type_title"
                required />
            <span className="title-input-error popup__error"></span>
            <input
                value={title || ''}
                onChange={handleTitleChange}
                // id="title-input"
                type="number"
                placeholder="Row"
                name="popup__title"
                className="popup__text popup__text_type_title"
                required />
            <span className="title-input-error popup__error"></span> */}

            <table className="popup__table">
                <tbody>
                    <tr className="popup__table-row">
                        <td className="popup__table-cell">1
                        <input
                                type="checkbox"
                                id="checkbox 1"
                                required />
                        </td>
                        <td className="popup__table-cell">2
                        <input
                                type="checkbox"
                                id="checkbox 2"
                                required />
                        </td>
                        <td className="popup__table-cell">3
                        <input
                                type="checkbox"
                                id="checkbox 3"
                                required />
                        </td>
                        <td className="popup__table-cell">4
                        <input
                                type="checkbox"
                                id="checkbox 4"
                                required />
                        </td>
                        <td className="popup__table-cell">5
                        <input
                                type="checkbox"
                                id="checkbox 5"
                                required />
                        </td>
                        <td className="popup__table-cell">6
                        <input
                                type="checkbox"
                                id="checkbox 6"
                                required />
                        </td>
                        <td className="popup__table-cell">7
                        <input
                                type="checkbox"
                                id="checkbox 7"
                                required />
                        </td>

                    </tr>
                </tbody>
            </table>
        </PopupWithForm>
    );
}

export default AddPopup;
