import React, { useEffect } from 'react';
import * as api from '../../utils/api.js';

function Class({ id, slots }) {
    const [theClass, setTheClass] = React.useState({});

    useEffect(() => {
        getClass();
    }, [id]);

    function getClass() {
        return api.getClass(id)
            .then((data) => {
                console.log(data)
                setTheClass(data);
            })
            .catch(err => console.log(err));
    }

    function checkSlot(id) {
        console.log(theClass.availableSlots)
        if (!theClass.availableSlots.includes(id)) {
            return "-";
        }
        return "";
    }

    return (
        <section className="class">
            <h3 className="class_title">Расписание аудитории {theClass.name}</h3>
            <table className="table">
                <tbody>
                    <tr className="table-row">
                        <th className="table-head"></th>
                        <th className="table-head">Mon</th>
                        <th className="table-head">Tue</th>
                        <th className="table-head">Wed</th>
                        <th className="table-head">Thu</th>
                        <th className="table-head">Fri</th>
                        <th className="table-head">Sat</th>
                        <th className="table-head">Sun</th>
                    </tr>

                    <tr className="table-row">
                        <th className="table-head">9:00</th>

                        <td id="1" className="table-cell">{checkSlot(id)}</td>
                        <td id="2" className="table-cell">{checkSlot(id)}</td>
                        {/* <td id="3" className="table-cell">{checkSlot(id)}</td>
                        <td id="4" className="table-cell">{checkSlot(id)}</td>
                        <td id="5" className="table-cell">{checkSlot(id)}</td>
                        <td id="6" className="table-cell">{checkSlot(id)}</td>
                        <td id="7" className="table-cell">{checkSlot(id)}</td> */}
                    </tr>
                </tbody>
            </table>
        </section>
    )
}

export default Class;
