import React, { useEffect } from 'react';
import { Link, Route, useHistory } from 'react-router-dom';
import * as api from '../../utils/api.js';

function ViewClasses({ onClick }) {
    const [classes, setClasses] = React.useState([]);
    const [theClass, setTheClass] = React.useState({});
    const [slots, setSlots] = React.useState([]);
    const history = useHistory();

    useEffect(() => {
        getClasses();
        getSlots();
    }, []);

    useEffect(() => {
        if (theClass.id) {
            console.log('CLASS', theClass)
            history.push(`/classes/${theClass.id}`);
        }
    }, [theClass]);

    function getClasses() {
        return api.getClasses()
            .then((data) => {
                console.log(data)
                setClasses(data);
            })
            .catch(err => console.log(err));
    }

    function getDay(id) {
        switch (id) {
            case 0:
                return "Пн";
            case 1:
                return "Вт";
            case 2:
                return "Ср";
            case 3:
                return "Чт";
            case 4:
                return "Пн";
            case 5:
                return "Сб";
            case 6:
                return "Вс";
        }
    }

    function getTime(time) {
        switch (time) {
            case 540:
                return "9:00";
            case 650:
                return "10:50";
            case 760:
                return "12:40";
            case 870:
                return "14:30";
            case 980:
                return "16:20";
            case 1090:
                return "18:10";
            case 1200:
                return "20:00";
        }
    }

    function getSlots() {
        return api.getSlots()
            .then((data) => {
                setSlots(data);
            })
            .catch(err => console.log(err));
    }

    function handleClick(e) {
        classes.forEach(c => {
            if (c.id.toString() === e.target.id) {
                setTheClass(c);
            }
        });
    }

    return (
        <section className="view-classes">
            {classes.map((item) => {
                console.log(item)
                return (
                    <button id={item.id} className="view__link" onClick={handleClick}>{item.name}</button>
                )
            })}
        </section>
    );
}

export default ViewClasses;
