import React, { useEffect } from 'react';
import { Link, Route } from 'react-router-dom';
import Class from '../Class/Class';
import * as api from '../../utils/api.js';

function ViewClasses() {
    const [classes, setClasses] = React.useState([]);
    const [theClass, setTheClass] = React.useState({});
    const [slots, setSlots] = React.useState([]);

    useEffect(() => {
        getClasses();
        getSlots();
    }, []);

    useEffect(() => {
        console.log('setting the class')
    }, [theClass]);

    function getClasses() {
        return api.getClasses()
            .then((data) => {
                console.log(data)
                setClasses(data);
            })
            .catch(err => console.log(err));
    }

    function getDay(id) {
        switch (id) {
            case 0:
                return "Пн";
            case 1:
                return "Вт";
            case 2:
                return "Ср";
            case 3:
                return "Чт";
            case 4:
                return "Пн";
            case 5:
                return "Сб";
            case 6:
                return "Вс";
        }
    }

    function getTime(time) {
        switch (time) {
            case 540:
                return "9:00";
            case 650:
                return "10:50";
            case 760:
                return "12:40";
            case 870:
                return "14:30";
            case 980:
                return "16:20";
            case 1090:
                return "18:10";
            case 1200:
                return "20:00";
        }
    }

    function getSlots() {
        return api.getSlots()
            .then((data) => {
                setSlots(data);
            })
            .catch(err => console.log(err));
    }

    return (
        <>
            <section className="view-classes">
                {classes.map((item) => {
                    console.log(item)
                    return (
                        <Link to={`/classes/${item.id}`} className="view__link" onClick={() => {
                            console.log('SET THE CLASS', item)
                            setTheClass(item)
                        }}>{item.name}</Link>
                    )
                })}
            </section>

            <Route
                path="/classes/:id"
                theClass={theClass}
                component={Class}
            />
        </>
    );
}

export default ViewClasses;
